import React, { useState, createContext, useEffect } from 'react';

export const TransparentaContext = createContext();

export function TransparentaProvider({ children }) {
	const [transOpen, setTransOpen] = useState(false);
	const [footer, setFooter] = useState(null);

	useEffect(async () => {
		if (localStorage.getItem('footer')) {
			setFooter(localStorage.getItem('footer'));
			return null;
		} else {
			const data = await fetch(`${process.env.API_URL}/footer`)
				.then((res) => res.json())
				.catch((err) => console.log(err));
			localStorage.setItem('footer', data);
			setFooter(footer);
			return null;
		}
	}, []);

	const getFooter = () => {};

	return (
		<TransparentaContext.Provider
			value={{ transOpen, setTransOpen, footer, getFooter }}
		>
			{children}
		</TransparentaContext.Provider>
	);
}
