import { useState } from 'react';
import Image from 'next/image';
import Link from 'next/link';
import { AnimatePresence, motion } from 'framer-motion';
import ActiveLink from '../components/ActiveLink';

const noScroll = require('no-scroll');

const menuVariants = {
	init: {
		opacity: 0
	},
	anim: {
		opacity: 1
	},
	end: {
		opacity: 0
	}
};

function ResNav() {
	const [open, setOpen] = useState(false);

	const handleMenu = () => {
		noScroll.toggle();
	};

	return (
		<>
			<nav className="w-full flex items-center justify-between px-35 py-35 relative z-50">
				<div className="mainLogo flex items-center justify-center w-auto">
					<Link href="/">
						<a>
							<Image
								src="/transparenta-logo.svg"
								alt="Transparenta Logo"
								layout="intrinsic"
								width="200"
								height="17px"
							/>
						</a>
					</Link>
				</div>
				<button
					className="bg-gray-100 outline-none"
					onClick={() => setOpen(!open)}
				>
					{open ? (
						<Image
							className="outline-none"
							src="/close.svg"
							alt="Menú"
							layout="intrinsic"
							width="25"
							height="20"
						/>
					) : (
						<Image
							className="outline-none"
							src="/burger.svg"
							alt="Menú"
							layout="intrinsic"
							width="25"
							height="20"
						/>
					)}
				</button>
			</nav>
			<AnimatePresence exitBeforeEnter>
				{open && (
					<motion.div
						initial="init"
						animate="anim"
						exit="end"
						variants={menuVariants}
						className="mobile-menu z-40 h-screen w-screen fixed left-0 top-0 bg-red-500 pointer-events-none flex items-center justify-center flex-col"
					>
						<ul>
							<li className="font-bold uppercase hover:text-main">
								<ActiveLink
									activeClassName="text-main"
									href="/proactiva"
								>
									<a>Transparencia proactiva</a>
								</ActiveLink>
							</li>
						</ul>
					</motion.div>
				)}
			</AnimatePresence>
		</>
	);
}

export default ResNav;
