export async function getServerSideProps(context) {
	const data = await fetch(`${process.env.API_URL}/services`).then((res) =>
		res.json()
	);
	if (!data) {
		return {
			props: { error: 404, status: 500 }
		};
	}
	return {
		props: {
			data
		}
	};
}
