import { useState } from 'react';
import Image from 'next/image';
import Link from 'next/link';
import { AnimatePresence, motion } from 'framer-motion';
import ActiveLink from '../components/ActiveLink';

const noScroll = require('no-scroll');

const iconVariants = {
	init: {
		scaleX: 0
	},
	anim: {
		scaleX: 1
	},
	end: {
		scaleX: 0
	}
};

const menuVariants = {
	init: {
		opacity: 0,
		y: '-100%'
	},
	anim: {
		opacity: 1,
		y: 0,
		transition: {
			ease: 'easeInOut'
		}
	},
	end: {
		opacity: 0,
		y: '-120%',
		transition: {
			ease: 'easeInOut'
		}
	}
};

function ResNav() {
	const [open, setOpen] = useState(false);

	const handleMenu = () => {
		noScroll.toggle();
		setOpen(!open);
	};

	return (
		<>
			<nav className="w-full flex items-center justify-between px-35 py-35 z-50 fixed left-0 top-0">
				<div className="mainLogo flex items-center justify-center w-auto">
					<Link href="/">
						<a>
							<Image
								src="/transparenta-logo.svg"
								alt="Transparenta Logo"
								layout="intrinsic"
								width="200"
								height="17px"
							/>
						</a>
					</Link>
				</div>
				<button
					className="bg-gray-100 outline-none"
					onClick={handleMenu}
					style={{
						outline: 'none !important'
					}}
				>
					<AnimatePresence exitBeforeEnter>
						{open ? (
							<motion.div variants={iconVariants}>
								<Image
									className="outline-none bg-transparent"
									src="/close.svg"
									alt="Menú"
									layout="intrinsic"
									width="25"
									height="20"
								/>
							</motion.div>
						) : (
							<motion.div>
								<Image
									className="outline-none bg-transparent"
									src="/burger.svg"
									alt="Menú"
									layout="intrinsic"
									width="25"
									height="20"
								/>
							</motion.div>
						)}
					</AnimatePresence>
				</button>
			</nav>
			<AnimatePresence exitBeforeEnter>
				{open && (
					<motion.div
						initial="init"
						animate="anim"
						exit="end"
						variants={menuVariants}
						className="mobile-menu z-40 h-screen w-screen fixed left-0 top-0 bg-white pointer-events-none flex items-center justify-center flex-col"
					>
						<ul>
							<li className="font-bold uppercase hover:text-main">
								<ActiveLink
									activeClassName="text-main"
									href="/proactiva"
								>
									<a>Transparencia proactiva</a>
								</ActiveLink>
							</li>
						</ul>
					</motion.div>
				)}
			</AnimatePresence>
		</>
	);
}

export default ResNav;
