										{/* <input
											type="text"
											name="sector"
											onChange={handleChange}
											onBlur={handleBlur}
											value={values.sector}
											placeholder="Sector"
											className="w-full bg-transparent text-black border-3 border-black py-1 px-2 shadow-transparentaSmall mb-6"
										/> */}
										{errors.email && (
											<p className="text-red-500 text-xs uppercase text-left w-full">
