import { useState } from 'react';
import Image from 'next/image';
import Link from 'next/link';
import ActiveLink from '../components/ActiveLink';
import { AnimatePresence, motion } from 'framer-motion';
import { Formik } from 'formik';

const menuVariants = {
	init: {
		opacity: 0,
		y: '-100%'
	},
	anim: {
		opacity: 1,
		y: 0,
		transition: {
			ease: 'easeInOut'
		}
	},
	end: {
		opacity: 0,
		y: '-120%',
		transition: {
			ease: 'easeInOut'
		}
	}
};

function Nav() {
	const [transparenta, setTransparenta] = useState(false);

	const handleTransparenta = () => {
		setTransparenta(!transparenta);
	};

	return (
		<>
			<nav className="w-full flex items-center justify-between py-35 px-16">
				<div className="mainLogo flex items-center justify-center w-1/4">
					<Link href="/">
						<a>
							<Image
								src="/transparenta-logo.svg"
								alt="Transparenta Logo"
								layout="intrinsic"
								width="350px"
								height="28.16px"
							/>
						</a>
					</Link>
				</div>
				<ul className="nav-items flex items-center justify-end w-3/4 text-black text-sm">
					<li className="font-bold uppercase mr-6 hover:text-main">
						<ActiveLink
							activeClassName="text-main"
							href="/proactiva"
						>
							<a>Transparencia proactiva</a>
						</ActiveLink>
					</li>
					<li className="font-bold uppercase mr-6 hover:text-main">
						<ActiveLink
							activeClassName="text-main"
							href="/producto"
						>
							<a>Producto</a>
						</ActiveLink>
					</li>
					<li className="font-bold uppercase mr-6 hover:text-main">
						<ActiveLink
							activeClassName="text-main"
							href="/servicios"
						>
							<a>Servicios</a>
						</ActiveLink>
					</li>
					<li
						onClick={handleTransparenta}
						className="font-bold uppercase py-2 px-4 bg-black text-white hover:bg-main hover:text-black cursor-pointer transparenta-btn-toblack shadow-none hover:shadow-transparentaSmall transition duration-200 ease-in-out"
					>
						Obt√©n Transparenta
					</li>
				</ul>
			</nav>
			<AnimatePresence exitBeforeEnter>
				{transparenta && (
					<motion.div
						key="contact-opened"
						initial="init"
						animate="anim"
						exit="end"
						variants={menuVariants}
						className="mobile-menu z-40 h-screen w-screen fixed left-0 top-0 bg-white flex items-center justify-start flex-col border-bottom-2 border-main overflow-y-auto px-10 pt-20"
					>
						<div
							className="close-transparenta"
							onClick={handleTransparenta}
						>
							<svg
								xmlns="http://www.w3.org/2000/svg"
								width="32"
								height="32"
								fill="none"
								stroke="#484d51"
								stroke-linecap="round"
								stroke-linejoin="round"
								stroke-width="3"
								viewBox="0 0 32 32"
							>
								<path d="M2 30 L30 2 M30 30 L2 2" />
							</svg>
						</div>
						<h3 className="text-3xl text-main font-bold w-full text-left">
							Contacto
						</h3>
						<Formik
							initialValues={{
								name: '',
								orgName: '',
								sector: '',
								services: '',
								email: '',
								text: ''
							}}
							validate={(values) => {
								const errors = {};
								if (!values.name) {
									errors.name = 'Campo obligatorio';
								}
								if (!values.orgName) {
									errors.orgName = 'Campo obligatorio';
								}
								if (!values.sector) {
									errors.sector = 'Campo obligatorio';
								}
								if (!values.services) {
									errors.services = 'Campo obligatorio';
								}
								if (!values.text) {
									errors.text = 'Campo obligatorio';
								}
								if (!values.email) {
									errors.email = 'Campo obligatorio';
								} else if (
									!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(
										values.email
									)
								) {
									errors.email = 'Correo inv√°lido';
								}
								return errors;
							}}
							onSubmit={(values, { setSubmitting }) => {
								alert('enviando');
								setTimeout(() => {
									alert(JSON.stringify(values, null, 2));
									setSubmitting(false);
								}, 400);
							}}
						>
							{({
								values,
								errors,
								touched,
								handleChange,
								handleBlur,
								handleSubmit,
								isSubmitting
							}) => (
								<form
									onSubmit={handleSubmit}
									className="w-full flex flex-col items-center justify-start pointer-events-auto pt-4 pb-10"
								>
									{errors.name && (
										<p className="text-red-500 text-xs uppercase text-left w-full">
											{errors.name}
										</p>
									)}
									<input
										type="text"
										name="name"
										onChange={handleChange}
										onBlur={handleBlur}
										value={values.name}
										placeholder="Nombre"
										className="w-full bg-transparent text-black border-2 border-black py-1 px-2 shadow-transparentaSmall mb-6"
									/>
									{errors.orgName && (
										<p className="text-red-500 text-xs uppercase text-left w-full">
											{errors.orgName}
										</p>
									)}
									<input
										type="text"
										name="orgName"
										onChange={handleChange}
										onBlur={handleBlur}
										value={values.orgName}
										placeholder="Nombre de la organizaci√≥n"
										className="w-full bg-transparent text-black border-2 border-black py-1 px-2 shadow-transparentaSmall mb-6"
									/>
									{errors.sector && (
										<p className="text-red-500 text-xs uppercase text-left w-full">
											{errors.sector}
										</p>
									)}
									<input
										type="text"
										name="sector"
										onChange={handleChange}
										onBlur={handleBlur}
										value={values.sector}
										placeholder="Sector"
										className="w-full bg-transparent text-black border-2 border-black py-1 px-2 shadow-transparentaSmall mb-6"
									/>
									{errors.sector && (
										<p className="text-red-500 text-xs uppercase text-left w-full">
											{errors.sector}
										</p>
									)}
									<input
										type="text"
										name="sector"
										onChange={handleChange}
										onBlur={handleBlur}
										value={values.sector}
										placeholder="Servicios de inter√©s"
										className="w-full bg-transparent text-black border-2 border-black py-1 px-2 shadow-transparentaSmall mb-6"
									/>
									{errors.email && (
										<p className="text-red-500 text-xs uppercase text-left w-full">
											{errors.email}
										</p>
									)}
									<input
										type="email"
										name="email"
										onChange={handleChange}
										onBlur={handleBlur}
										value={values.email}
										placeholder="Servicios de inter√©s"
										className="w-full bg-transparent text-black border-2 border-black py-1 px-2 shadow-transparentaSmall mb-6"
									/>
									{errors.text && (
										<p className="text-red-500 text-xs uppercase text-left w-full">
											{errors.text}
										</p>
									)}
									<textarea
										name="text"
										cols="30"
										rows="10"
										placeholder="Cu√©ntanos m√°s"
										className="w-full"
										onChange={handleChange}
										onBlur={handleBlur}
										className="w-full bg-transparent text-black border-2 border-black py-1 px-2 shadow-transparentaSmall"
									>
										{values.text}
									</textarea>
									<div className="btn-holder flex items-center justify-center w-full mt-10">
										<button className="bg-black py-3 w-full text-center text-white font-bold after-shadow-outline">
											Enviar
										</button>
									</div>
								</form>
							)}
						</Formik>
					</motion.div>
				)}
			</AnimatePresence>
		</>
	);
}

export default Nav;
