import Head from 'next/head';
import styled from 'styled-components';
import { motion } from 'framer-motion';

export default function Producto() {
	return (
		<>
			<Head>
				<title>Transparenta | Producto</title>
			</Head>
			<motion.main
				initial={{ opacity: 0 }}
				animate={{ opacity: 1 }}
				exit={{ opacity: 0 }}
				className="container mx-auto flex items-center justify-center"
			>
				<section className="header w-full lg:w-10/12 mb-20 mt-16">
					<h1 className="text-xl md:text-3xl lg:text-5xl text-main font-bold w-full">
						Licencia Transparenta
					</h1>
					<p className="text-black text-lg md:text-xl lg:text-3xl mt-4 font-bold w-full">
						¿Qué es? <br />
						Construimos un sistema de gestión personalizable que
						utiliza herramientas flexibles enfocadas en
						transparentar procesos e información contextualizada y
						presentada de una forma visualmente amigable para
						facilitar su comprensión. Las herramientas de
						Transparenta se adaptan a las necesidades de cada
						organización, automatizando la comunicación de la
						información, enfocándonos en crear plataformas fáciles
						de operar.
					</p>
				</section>
			</motion.main>
		</>
	);
}
